import React from 'react';
import { Storage } from 'aws-amplify'
import { v4 as uuidv4 } from 'uuid';

import { API, graphqlOperation } from "aws-amplify";
import * as mutations from './graphql/mutations';


import {
  AutoField,
  AutoForm,
  ErrorField,
  DateField,
  RadioField,
  SubmitField
} from 'uniforms-semantic';

import RequestSchema from './SaRequestSchema';

const style = {
  margin: 200,
};

// Get the query parameters and default them into the form
let search = window.location.search;
let params = new URLSearchParams(search);
// Salesforce account 
let account = params.get('account');
let name = params.get('name');
console.log(name);

var handleAdd = (doc) => {

  // generate unique request# and file name for S3
  var requestID = uuidv4();
  var s3fileName = 'sa-requests/request-' + requestID + '.json' 
  console.log('Request ID:', requestID)
  console.log('File Name:', s3fileName);

  console.log("Here is the document",doc)
  // Just using a placeholder user id since there is no login currently
  const post = {accountID: doc.accountID, accountName: doc.accountName, activityInfo: doc.activityInfo}
  console.log(post)
  //api.posts.createPost({ post: post}).then(json => displayMessage(json))

  // save the form data to S3 cloud Storage
  var addToStorage = () => {
    Storage.put(s3fileName, doc)
      .then (result => {
        console.log('result: ', result)
      })
      .catch(err => console.log('error: ', err));
  }
  addToStorage();

  //write data to the API using Graphql
  (async () => {
    const newRequest = await API.graphql(graphqlOperation(mutations.createRequest, {input: doc}));
  })();

}


export default function RequestForm() {

  return (
    <AutoForm style={style} schema={RequestSchema} onSubmit={doc => handleAdd(doc)}>
      <h2>SA Activity/Support Request</h2>
      <Form.Group widths="equal">
        <AutoField name="accountID" value={account}/>
        <ErrorField name="accountID" errorMessage="Account ID is required"/>
        <AutoField name="accountName" value={name}/>
        <ErrorField name="accountName" errorMessage="Account name is required"/>
      </Form.Group>  
      <h3>Activity Details</h3>
      <span></span>
      <Form.Group widths="equal">    
        <AutoField name="activityType" />
        <DateField name="activityDate" />
      </Form.Group>  
      <AutoField name="activityInfo" />
      <RadioField name="activityLocation" />
      <SubmitField />
    </AutoForm>
  );
}
